  TestingUtils.testGridTemplateAreas('grid', '"a   b"   "c   d"', ['"a b" "c d"', '"a   b" "c   d"']);
  TestingUtils.testGridTemplateAreas('grid', '"a b""c d"', '"a b" "c d"');
  TestingUtils.testGridTemplateAreas('grid', '"a b"\t"c d"', '"a b" "c d"');
